const Client = require('../index').Client

let client = new Client({server: '127.0.0.1', port: 30210, type: 'udp4', timeout: 32 * 1000})

client.on('listen', data => {
  console.log('UDP Client Listening', data)
})

client.on('connect', ()=> {
  console.log('Connected')
})

client.on('error', err => {
  console.log(err)
})

// Simple chat app
client.on('data', data => {
  console.log(data.toString())
})
process.stdin.on('data', data => {
  client.send(data)
})

